import axios from 'axios'

import { useQuery } from 'react-query'
import {
    linkPreviewKey,
    urlPreviewGeneratorLink,
} from '../../../constants/links'

export const useGetPreview = (url) => {
    const { isFetching, data, error, status } = useQuery(
        ['urlPreview', url],
        () =>
            axios.post(urlPreviewGeneratorLink, {
                key: linkPreviewKey,
                q: url,
            }),
        { keepPreviousData: true, staleTime: 120000 }
    )
            console.log({ error, status })
            return { isFetching, data: data?.data }
}
